    val listData: ArrayList<Shoes>
        get() {
            val list = arrayListOf<Shoes>()
            for (position in shoesNames.indices){
                val shoes = Shoes()
                shoes.name = shoesNames[position]
                shoes.brand = shoesBrand[position]
                shoes.detail = shoesDetails[position]
                shoes.photo = shoesImages[position]
                shoes.photo1 = shoesImages1[position]
                shoes.photo2 = shoesImages2[position]
                shoes.photo3 = shoesImages3[position]
                shoes.photo4 = shoesImages4[position]
                shoes.photo5 = shoesImages5[position]
                list.add(shoes)
            }
            return list
        }
